import { PayrocClient } from "../../src/Client";
import { mockServerPool } from "../mock-server/MockServerPool";
import { mockBearer } from "./mockAuth";

describe("AuthClient", () => {
    test("retrieveToken", async () => {
        const server = mockServerPool.createServer();
        mockBearer(server);

        const client = new PayrocClient({
            maxRetries: 0,
            apiKey: "x-api-key",
            environment: { api: server.baseUrl, identity: server.baseUrl },
        });

        const rawResponseBody = {
            access_token: "access_token",
            token_type: "token_type",
            expires_in: 1,
            scope: "scope",
        };
        server
            .mockEndpoint()
            .post("/authorize")
            .header("x-api-key", "x-api-key")
            .respondWith()
            .statusCode(200)
            .jsonBody(rawResponseBody)
            .build();

        const response = await client.auth.retrieveToken({
            "x-api-key": "x-api-key",
        });
        expect(response).toEqual({
            access_token: "access_token",
            token_type: "token_type",
            expires_in: 1,
            scope: "scope",
        });
    });
});
