    test("lookupBin (1)", async () => {
        const server = mockServerPool.createServer();
        mockBearer(server);

        const client = new PayrocClient({
            maxRetries: 0,
            apiKey: "x-api-key",
            environment: { api: server.baseUrl, identity: server.baseUrl },
        });
        const rawRequestBody = {
            processingTerminalId: "1234001",
            card: {
                type: "card",
                cardDetails: {
                    entryMethod: "raw",
                    device: { model: "bbposChp", serialNumber: "1850010868" },
                    rawData: "A1B2C3D4E5F67890ABCD1234567890ABCDEF1234567890ABCDEF1234567890ABCDEF",
                },
            },
        };
        const rawResponseBody = {
            type: "MASTERCARD",
            cardNumber: "453985******7062",
            country: "US",
            currency: "USD",
            debit: false,
            surcharging: {
                allowed: true,
                amount: 87,
                percentage: 3,
                disclosure: "A 3% surcharge is applied to cover processing fees.",
            },
        };
        server
            .mockEndpoint()
            .post("/cards/bin-lookup")
            .jsonBody(rawRequestBody)
            .respondWith()
            .statusCode(200)
            .jsonBody(rawResponseBody)
            .build();

        const response = await client.payments.cards.lookupBin({
            processingTerminalId: "1234001",
            card: {
                type: "card",
                cardDetails: {
                    entryMethod: "raw",
                    device: {
                        model: "bbposChp",
                        serialNumber: "1850010868",
                    },
                    rawData: "A1B2C3D4E5F67890ABCD1234567890ABCDEF1234567890ABCDEF1234567890ABCDEF",
                },
            },
        });
        expect(response).toEqual({
            type: "MASTERCARD",
            cardNumber: "453985******7062",
            country: "US",
            currency: "USD",
            debit: false,
            surcharging: {
                allowed: true,
                amount: 87,
                percentage: 3,
                disclosure: "A 3% surcharge is applied to cover processing fees.",
            },
        });
    });

